                update.setVisibility(View.VISIBLE);
            remind_me.setVisibility(View.GONE);
            reassign_note.setVisibility(View.GONE);
            task_accept_layout.setVisibility(View.GONE);
            calen_picker.setVisibility(View.VISIBLE);
